import React, { useState, useEffect } from "react";
import axios from "axios";
import Historico from './Historico'; // Importar el componente Historico

function App() {
  const [sensorData, setSensorData] = useState({ temperature: null, humidity: null });
  const [parametros, setParametros] = useState({
    temperatura_min: 20,
    temperatura_max: 25,
    humedad_min: 40,
    humedad_max: 60
  });
  const [loading, setLoading] = useState(true);
  const [periodo, setPeriodo] = useState(30); // Período por defecto

  const BACKEND_URL = "http://192.168.1.10:5001"; // Cambiar a la URL correcta

  // Obtener datos del sensor
  const fetchSensorData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${BACKEND_URL}/sensor-data`);
      setSensorData(response.data);
    } catch (error) {
      console.error("Error al obtener datos del sensor:", error);
    }
    setLoading(false);
  };

  // Controlar actuadores
  const controlActuador = async (actuador, accion) => {
    try {
      await axios.post(`${BACKEND_URL}/control/${actuador}/${accion}`);
      fetchSensorData();  // Actualizar datos después de cambiar actuador
    } catch (error) {
      console.error("Error controlando actuador:", error);
    }
  };

  // Actualizar parámetros de control
  const actualizarParametros = async () => {
    try {
      await axios.post(`${BACKEND_URL}/parametros_control`, parametros);
      console.log("Parámetros actualizados:", parametros);
    } catch (error) {
      console.error("Error actualizando parámetros:", error);
    }
  };

  // Actualizar datos periódicamente
  useEffect(() => {
    fetchSensorData();
    const interval = setInterval(fetchSensorData, 5000); // Cada 5 segundos
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col md:flex-row justify-between">
      {/* Panel principal */}
      <div className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-md flex-1">
        <h1 className="text-3xl font-bold text-center mb-6">Estación Meteorológica IoT</h1>

        {/* Datos del Sensor */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">Datos en Tiempo Real</h2>
          {loading ? (
            <p className="text-gray-500">Cargando datos...</p>
          ) : (
            <div className="text-lg">
              <p>🌡️ Temperatura: {sensorData.temperature} °C</p>
              <p>💧 Humedad: {sensorData.humidity} %</p>
            </div>
          )}
        </div>

        {/* Parámetros de Control */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">Actualizar Parámetros</h2>
          <div className="space-y-4">
            <label className="block">
              Temperatura Mínima:
              <input
                type="number"
                value={parametros.temperatura_min}
                onChange={(e) => setParametros({ ...parametros, temperatura_min: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <label className="block">
              Temperatura Máxima:
              <input
                type="number"
                value={parametros.temperatura_max}
                onChange={(e) => setParametros({ ...parametros, temperatura_max: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <label className="block">
              Humedad Mínima:
              <input
                type="number"
                value={parametros.humedad_min}
                onChange={(e) => setParametros({ ...parametros, humedad_min: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <label className="block">
              Humedad Máxima:
              <input
                type="number"
                value={parametros.humedad_max}
                onChange={(e) => setParametros({ ...parametros, humedad_max: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <button
              onClick={actualizarParametros}
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Actualizar Parámetros
            </button>
          </div>
        </div>

        {/* Histórico */}
        <Historico />
      </div>

      {/* Panel de control flotante con botones */}
      <div className="flex flex-col bg-gray-800 text-white p-4 space-y-4 rounded-md shadow-lg fixed right-4 bottom-4 md:relative md:w-72">
        <h2 className="text-xl font-semibold mb-2">Controles de Actuadores</h2>

        {/* Botones organizados horizontalmente */}
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => controlActuador("led", "on")}
            className="bg-green-500 text-white text-sm px-3 py-2 rounded-md hover:bg-green-700 w-24"
          >
            Encender LED
          </button>
          <button
            onClick={() => controlActuador("led", "off")}
            className="bg-red-500 text-white text-sm px-3 py-2 rounded-md hover:bg-red-700 w-24"
          >
            Apagar LED
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => controlActuador("iluminacion_sala_control", "on")}
            className="bg-green-500 text-white text-sm px-3 py-2 rounded-md hover:bg-green-700 w-24"
          >
            Encender Ilum. Sala
          </button>
          <button
            onClick={() => controlActuador("iluminacion_sala_control", "off")}
            className="bg-red-500 text-white text-sm px-3 py-2 rounded-md hover:bg-red-700 w-24"
          >
            Apagar Ilum. Sala
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => controlActuador("intraccion_aire", "on")}
            className="bg-green-500 text-white text-sm px-3 py-2 rounded-md hover:bg-green-700 w-24"
          >
            Encender Intracción Aire
          </button>
          <button
            onClick={() => controlActuador("intraccion_aire", "off")}
            className="bg-red-500 text-white text-sm px-3 py-2 rounded-md hover:bg-red-700 w-24"
          >
            Apagar Intracción Aire
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => controlActuador("extractor", "on")}
            className="bg-green-500 text-white text-sm px-3 py-2 rounded-md hover:bg-green-700 w-24"
          >
            Encender Extractor
          </button>
          <button
            onClick={() => controlActuador("extractor", "off")}
            className="bg-red-500 text-white text-sm px-3 py-2 rounded-md hover:bg-red-700 w-24"
          >
            Apagar Extractor
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => controlActuador("ventilacion", "on")}
            className="bg-green-500 text-white text-sm px-3 py-2 rounded-md hover:bg-green-700 w-24"
          >
            Encender Ventilación
          </button>
          <button
            onClick={() => controlActuador("ventilacion", "off")}
            className="bg-red-500 text-white text-sm px-3 py-2 rounded-md hover:bg-red-700 w-24"
          >
            Apagar Ventilación
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => controlActuador("bomba", "on")}
            className="bg-green-500 text-white text-sm px-3 py-2 rounded-md hover:bg-green-700 w-24"
          >
            Encender Bomba
          </button>
          <button
            onClick={() => controlActuador("bomba", "off")}
            className="bg-red-500 text-white text-sm px-3 py-2 rounded-md hover:bg-red-700 w-24"
          >
            Apagar Bomba
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
