import React, { useState, useEffect } from "react";
import axios from "axios";
import Historico from './Historico'; // Importar el componente Historico

function App() {
  const [sensorData, setSensorData] = useState({ temperature: null, humidity: null });
  const [parametros, setParametros] = useState({
    temperatura_min: 20,
    temperatura_max: 25,
    humedad_min: 40,
    humedad_max: 60
  });
  const [loading, setLoading] = useState(true);
  const [periodo, setPeriodo] = useState(30); // Per√≠odo por defecto

  const BACKEND_URL = "http://192.168.1.10:5001"; // Cambiar a la URL correcta

  // Obtener datos del sensor
  const fetchSensorData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${BACKEND_URL}/sensor-data`);
      setSensorData(response.data);
    } catch (error) {
      console.error("Error al obtener datos del sensor:", error);
    }
    setLoading(false);
  };

  // Controlar actuadores
  const controlActuador = async (actuador, accion) => {
    try {
      await axios.post(`${BACKEND_URL}/control/${actuador}/${accion}`);
      fetchSensorData();  // Actualizar datos despu√©s de cambiar actuador
    } catch (error) {
      console.error("Error controlando actuador:", error);
    }
  };

  // Actualizar par√°metros de control
  const actualizarParametros = async () => {
    try {
      await axios.post(`${BACKEND_URL}/parametros_control`, parametros);
      console.log("Par√°metros actualizados:", parametros);
    } catch (error) {
      console.error("Error actualizando par√°metros:", error);
    }
  };

  // Actualizar datos peri√≥dicamente
  useEffect(() => {
    fetchSensorData();
    const interval = setInterval(fetchSensorData, 5000); // Cada 5 segundos
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col md:flex-row justify-between bg-white" style={{ fontFamily: "Roboto, sans-serif" }}>

      {/* Panel principal */}
      <div className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-md flex-1">
        <header style={{ backgroundColor: "#000000", color: "#ffffff", padding: "10px", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <img src="https://imgur.com/J85jLe1" alt="Logo" style={{ height: "50px", marginRight: "10px" }} />
          <h1 style={{ fontSize: "24px", fontWeight: "normal", margin: "0" }}>Aerogrow - Proyecto de T√≠tulo v1.0.0</h1>
          <div>
            <a href="#" style={{ color: "#ffffff", marginRight: "20px" }}>Empresa</a>
            <a href="#" style={{ color: "#ffffff", marginRight: "20px" }}>Otros proyectos</a>
            <a href="#" style={{ color: "#ffffff", marginRight: "20px" }}>Noticias</a>
            <a href="#" style={{ color: "#ffffff" }}>Contacto</a>
          </div>
        </header>

        {/* Datos del Sensor */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">Datos en Tiempo Real</h2>
          {loading ? (
            <p className="text-gray-500">Cargando datos...</p>
          ) : (
            <div className="text-lg">
              <p>üå°Ô∏è Temperatura: {sensorData.temperature} ¬∞C</p>
              <p>üíß Humedad: {sensorData.humidity} %</p>
            </div>
          )}
        </div>

        {/* Par√°metros de Control */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">Actualizar Par√°metros</h2>
          <div className="space-y-4">
            <label className="block">
              Temperatura M√≠nima:
              <input
                type="number"
                value={parametros.temperatura_min}
                onChange={(e) => setParametros({ ...parametros, temperatura_min: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <label className="block">
              Temperatura M√°xima:
              <input
                type="number"
                value={parametros.temperatura_max}
                onChange={(e) => setParametros({ ...parametros, temperatura_max: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <label className="block">
              Humedad M√≠nima:
              <input
                type="number"
                value={parametros.humedad_min}
                onChange={(e) => setParametros({ ...parametros, humedad_min: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <label className="block">
              Humedad M√°xima:
              <input
                type="number"
                value={parametros.humedad_max}
                onChange={(e) => setParametros({ ...parametros, humedad_max: e.target.value })}
                className="mt-1 p-2 border rounded-md"
              />
            </label>
            <button
              onClick={actualizarParametros}
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Actualizar Par√°metros
            </button>
          </div>
        </div>

        {/* Hist√≥rico */}
        <Historico />
      </div>

      {/* Zona de Control flotante con botones */}
      <div className="flex flex-wrap gap-4 justify-between bg-black text-white p-6 rounded-md shadow-lg w-1/3">
        <div className="flex flex-col gap-4">
          {["LED", "Sala Control", "Intracci√≥n", "Extracci√≥n", "Ventilaci√≥n", "Riego", "Base", "Humidificador"].map((actuador, index) => (
            <button
              key={index}
              onClick={() => controlActuador(actuador.toLowerCase(), "toggle")}
              className="flex items-center justify-between bg-gray-800 text-white text-sm px-4 py-2 rounded-md hover:bg-gray-600 w-full"
              style={{ minHeight: "40px", fontSize: "12px", fontWeight: "normal" }}
            >
              {actuador}
            </button>
          ))}
        </div>
      </div>

      {/* Gr√°fico de Temperatura y Humedad */}
      <div className="w-full max-w-xs mx-auto">
        <div style={{ width: "500px", height: "300px", backgroundColor: "#f5f5f5", marginTop: "20px" }}>
          {/* Aqu√≠ va el componente de gr√°fico de temperatura y humedad */}
        </div>
      </div>

    </div>
  );
}

export default App;
