import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [sensorData, setSensorData] = useState({ temperature: null, humidity: null });
  const [parametros, setParametros] = useState({
    temperatura_min: 20,
    temperatura_max: 25,
    humedad_min: 40,
    humedad_max: 60
  });
  const [loading, setLoading] = useState(true);

  // URL del backend de la Raspberry Pi
  const BACKEND_URL = "http://192.168.1.10:5001";  // Puerto 5001 configurado correctamente

  // Función para obtener datos del sensor
  const fetchSensorData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${BACKEND_URL}/sensor-data`);
      setSensorData(response.data);
    } catch (error) {
      console.error("Error fetching sensor data:", error);
    }
    setLoading(false);
  };

  // Función para controlar actuadores
  const controlActuador = async (actuador, accion) => {
    console.log(`Enviando solicitud para ${accion} ${actuador}`);
    try {
      await axios.post(`${BACKEND_URL}/control/${actuador}/${accion}`);
      fetchSensorData();  // Actualizar datos después de cambiar actuador
    } catch (error) {
      console.error("Error controlando actuador:", error);
    }
  };

  // Función para actualizar parámetros de control
  const actualizarParametros = async () => {
    try {
      await axios.post(`${BACKEND_URL}/parametros_control`, parametros);
      console.log("Parámetros actualizados:", parametros);
    } catch (error) {
      console.error("Error actualizando parámetros:", error);
    }
  };

  // Efecto para obtener datos del sensor cada 5 segundos
  useEffect(() => {
    fetchSensorData();
    const interval = setInterval(fetchSensorData, 5000); // Actualiza cada 5 segundos
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-md">
      <h1 className="text-3xl font-bold text-center mb-6">Estación Meteorológica IoT</h1>

      {/* Datos del Sensor */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Datos del Sensor</h2>
        {loading ? (
          <p>Cargando datos...</p>
        ) : (
          <div className="text-lg">
            <p>Temperatura: {sensorData.temperature} °C</p>
            <p>Humedad: {sensorData.humidity} %</p>
          </div>
        )}
      </div>

      {/* Controles de Actuadores */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Controles de Actuadores</h2>
        <div className="grid grid-cols-2 gap-4">
          <button
            onClick={() => controlActuador("led", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Iluminación LED
          </button>
          <button
            onClick={() => controlActuador("led", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Iluminación LED
          </button>

          <button
            onClick={() => controlActuador("iluminacion_sala_control", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Iluminación Sala de Control
          </button>
          <button
            onClick={() => controlActuador("iluminacion_sala_control", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Iluminación Sala de Control
          </button>

          <button
            onClick={() => controlActuador("intraccion_aire", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Intracción de Aire
          </button>
          <button
            onClick={() => controlActuador("intraccion_aire", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Intracción de Aire
          </button>

          <button
            onClick={() => controlActuador("extractor", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Extractor
          </button>
          <button
            onClick={() => controlActuador("extractor", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Extractor
          </button>

          <button
            onClick={() => controlActuador("ventilacion", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Ventilación
          </button>
          <button
            onClick={() => controlActuador("ventilacion", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Ventilación
          </button>

          <button
            onClick={() => controlActuador("bomba", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Bomba de Agua
          </button>
          <button
            onClick={() => controlActuador("bomba", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Bomba de Agua
          </button>

          <button
            onClick={() => controlActuador("motor_base_giratoria", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Motor de Base Giratoria
          </button>
          <button
            onClick={() => controlActuador("motor_base_giratoria", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Motor de Base Giratoria
          </button>

          <button
            onClick={() => controlActuador("humidificador", "on")}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700"
          >
            Encender Humidificador
          </button>
          <button
            onClick={() => controlActuador("humidificador", "off")}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700"
          >
            Apagar Humidificador
          </button>
        </div>
      </div>

      {/* Parámetros de Control */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Actualizar Parámetros</h2>
        <div className="space-y-4">
          <label className="block">
            Temperatura Mínima:
            <input
              type="number"
              value={parametros.temperatura_min}
              onChange={(e) => setParametros({ ...parametros, temperatura_min: e.target.value })}
              className="mt-1 p-2 border rounded-md"
            />
          </label>
          <label className="block">
            Temperatura Máxima:
            <input
              type="number"
              value={parametros.temperatura_max}
              onChange={(e) => setParametros({ ...parametros, temperatura_max: e.target.value })}
              className="mt-1 p-2 border rounded-md"
            />
          </label>
          <label className="block">
            Humedad Mínima:
            <input
              type="number"
              value={parametros.humedad_min}
              onChange={(e) => setParametros({ ...parametros, humedad_min: e.target.value })}
              className="mt-1 p-2 border rounded-md"
            />
          </label>
          <label className="block">
            Humedad Máxima:
            <input
              type="number"
              value={parametros.humedad_max}
              onChange={(e) => setParametros({ ...parametros, humedad_max: e.target.value })}
              className="mt-1 p-2 border rounded-md"
            />
          </label>
          <button
            onClick={actualizarParametros}
            className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700"
          >
            Actualizar Parámetros
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
