import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [sensorData, setSensorData] = useState({ temperature: null, humidity: null });
  const [parametros, setParametros] = useState({
    temperatura_min: 20,
    temperatura_max: 25,
    humedad_min: 40,
    humedad_max: 60
  });
  const [loading, setLoading] = useState(true);

  // URL del backend de la Raspberry Pi
  const BACKEND_URL = "http://192.168.1.10:5001";  // Puerto 5001 configurado correctamente

  // Función para obtener datos del sensor
  const fetchSensorData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${BACKEND_URL}/sensor-data`);
      setSensorData(response.data);
    } catch (error) {
      console.error("Error fetching sensor data:", error);
    }
    setLoading(false);
  };

  // Función para controlar actuadores
  const controlActuador = async (actuador, accion) => {
    try {
      console.log(`Enviando solicitud para ${accion} ${actuador}`);  // Log para verificar en la consola
      await axios.post(`${BACKEND_URL}/control/${actuador}/${accion}`);
      fetchSensorData();  // Actualizar datos después de cambiar actuador
    } catch (error) {
      console.error("Error controlando actuador:", error);
    }
  };

  // Función para actualizar parámetros de control
  const actualizarParametros = async () => {
    try {
      await axios.post(`${BACKEND_URL}/parametros_control`, parametros);
      console.log("Parámetros actualizados:", parametros);
    } catch (error) {
      console.error("Error actualizando parámetros:", error);
    }
  };

  // Efecto para obtener datos del sensor cada 5 segundos
  useEffect(() => {
    fetchSensorData();
    const interval = setInterval(fetchSensorData, 5000); // Actualiza cada 5 segundos
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <h1>Estación Meteorológica IoT</h1>
      <div>
        <h2>Datos del Sensor</h2>
        {loading ? (
          <p>Cargando datos...</p>
        ) : (
          <div>
            <p>Temperatura: {sensorData.temperature} °C</p>
            <p>Humedad: {sensorData.humidity} %</p>
          </div>
        )}
      </div>

      <div>
        <h2>Controles de Actuadores</h2>
        <div>
          <button onClick={() => controlActuador("led", "on")}>Encender Iluminación LED</button>
          <button onClick={() => controlActuador("led", "off")}>Apagar Iluminación LED</button>
        </div>
        <div>
          <button onClick={() => controlActuador("sala_control", "on")}>Encender Iluminación Sala de Control</button>
          <button onClick={() => controlActuador("sala_control", "off")}>Apagar Iluminación Sala de Control</button>
        </div>
        <div>
          <button onClick={() => controlActuador("intraccion_aire", "on")}>Encender Intracción de Aire</button>
          <button onClick={() => controlActuador("intraccion_aire", "off")}>Apagar Intracción de Aire</button>
        </div>
        <div>
          <button onClick={() => controlActuador("extractor", "on")}>Encender Extractor</button>
          <button onClick={() => controlActuador("extractor", "off")}>Apagar Extractor</button>
        </div>
        <div>
          <button onClick={() => controlActuador("ventilacion", "on")}>Encender Ventilación</button>
          <button onClick={() => controlActuador("ventilacion", "off")}>Apagar Ventilación</button>
        </div>
        <div>
          <button onClick={() => controlActuador("bomba", "on")}>Encender Bomba de Agua</button>
          <button onClick={() => controlActuador("bomba", "off")}>Apagar Bomba de Agua</button>
        </div>
        <div>
          <button onClick={() => controlActuador("motor", "on")}>Encender Motor Base Giratoria</button>
          <button onClick={() => controlActuador("motor", "off")}>Apagar Motor Base Giratoria</button>
        </div>
        <div>
          <button onClick={() => controlActuador("humidificador", "on")}>Encender Humidificador</button>
          <button onClick={() => controlActuador("humidificador", "off")}>Apagar Humidificador</button>
        </div>
      </div>

      <div>
        <h2>Actualizar Parámetros</h2>
        <div>
          <label>
            Temperatura Mínima:
            <input
              type="number"
              value={parametros.temperatura_min}
              onChange={(e) => setParametros({ ...parametros, temperatura_min: e.target.value })}
            />
          </label>
        </div>
        <div>
          <label>
            Temperatura Máxima:
            <input
              type="number"
              value={parametros.temperatura_max}
              onChange={(e) => setParametros({ ...parametros, temperatura_max: e.target.value })}
            />
          </label>
        </div>
        <div>
          <label>
            Humedad Mínima:
            <input
              type="number"
              value={parametros.humedad_min}
              onChange={(e) => setParametros({ ...parametros, humedad_min: e.target.value })}
            />
          </label>
        </div>
        <div>
          <label>
            Humedad Máxima:
            <input
              type="number"
              value={parametros.humedad_max}
              onChange={(e) => setParametros({ ...parametros, humedad_max: e.target.value })}
            />
          </label>
        </div>
        <button onClick={actualizarParametros}>Actualizar Parámetros</button>
      </div>
    </div>
  );
}

export default App;
